mapper.insert:
		insert into goit_board( b_no
		                      , id
		                      , b_title
		                      , b_content
		                      , b_img
		                      , b_thumb)
              		  values( goit_board_seq.nextval
		                      , #{id}
		                      , #{b_title}
	                      	  , #{b_content}
	                      	  , #{b_img}
	                      	  , #{b_thumb})

mapper.update:
		update goit_board set b_title = #{b_title}
		 	                , b_img = #{b_img}
		 	                , b_thumb = #{b_thumb}
		 	                , b_content = #{b_content}
 		where b_no = #{b_no} and id = #{id}
